import React from 'react';
import Card from '../../Card';
import { SafeAreaView } from 'react-native';
import { useTheme } from '@shopify/restyle';
const Container = ({ containerStyle, cardProps, mode, children, }) => {
    const theme = useTheme();
    return mode === 'card' ? (<Card {...cardProps}>{children}</Card>) : (<SafeAreaView style={{ backgroundColor: theme.colors.background, ...containerStyle }}>{children}</SafeAreaView>);
};
export default Container;
