import { View, Text, StyleSheet } from 'react-native';
export const Desc = ({ desc, theme }) => {
    const styles = createStyles({
        desc: theme.colors.text,
    });
    if (Array.isArray(desc) && desc.length) {
        const descs = desc;
        return (<View>
        {descs.map((item, index) => (<Text style={styles.desc} key={index}>
            {item}
          </Text>))}
      </View>);
    }
    return <Text style={styles.desc}>{desc}</Text>;
};
function createStyles({ desc = '#5e6d82' }) {
    return StyleSheet.create({
        desc: {
            color: desc,
            fontSize: 14,
            marginTop: 10,
            lineHeight: 20,
        },
    });
}
