import { Fragment } from 'react';
import Icon from '../Icon';
export const IconCustom = (props) => {
    const { icon, size, color, theme } = props;
    if (icon) {
        if (typeof icon === 'string') {
            return <Icon name={icon} size={size ? size : 15} color={color ? color : 'red'}/>;
        }
        return <Fragment>{icon}</Fragment>;
    }
    return (<Icon name="circle-o" size={size ? size : 15} color={color ? color : theme.colors.primary_background || '#3578e5'}/>);
};
